const { BeforeAll, AfterAll } = require('@cucumber/cucumber');
const { Builder } = require('selenium-webdriver');

let driver;

BeforeAll(async function () {
    driver = new Builder().forBrowser('firefox').build();
    this.driver = driver;
});

AfterAll(async function () {
    await driver.quit();
});
